
public class yeah {
    public static short testParameter(short parameter)
    { System.out.println("Je suis une méthode qui travaille sur les short"); return (short) (parameter / 2); } public static int testParameter(int parameter) { System.out.println("Je suis une méthode qui travaille sur les int"); return parameter / 2; } public static float testParameter(float parameter) { System.out.println("Je suis une méthode qui travaille sur les float"); return parameter / 2f; } public static double testParameter(double parameter) { System.out.println("Je suis une méthode qui travaille sur les double"); return parameter / 2.0; } }
